(function verifyVaryHeader(varyValue = 'Accept, Accept-Encoding'){
  pm.test("Vary Header", function () {
    pm.response.to.have.header("Vary");
    pm.response.to.be.header("Vary", varyValue);
});
})

(function verifyStatus(status=200){
pm.test("Status code Header", function () {
    pm.response.to.have.status(status);
});
}
)

(function verifyCacheInfo(info='caching'){
pm.test("X-Cache-Info Header", function () {
    pm.response.to.have.header("X-Cache-Info");
    pm.response.to.be.header("X-Cache-Info", info);
});
})

(function verifyHeaders(varyValue = 'Accept, Accept-Encoding', status=200, 
contentType='application/json', headers='Link, X-TotalRecords, X-Release', 
encode='chunked', info='caching', xRelease, controlAllow){

pm.test("Vary Header", function () {
pm.response.to.have.header("Vary");
pm.response.to.be.header("Vary", varyValue);
});

pm.test("Status code Header", function () {
pm.response.to.have.status(status);
});

pm.test("Content-Type Header", function () {
pm.response.to.have.header("Content-Type");
pm.response.to.be.header("Content-Type", contentType);
});

pm.test("X-Release Header", function () {
pm.response.to.have.header("X-Release");
pm.response.to.be.header("X-Release", xRelease);
});

pm.test("Access-Control-Allow-Origin Header", function () {
pm.response.to.have.header("Access-Control-Allow-Origin");
pm.response.to.be.header("Access-Control-Allow-Origin", controlAllow);
});

pm.test("Access-Control-Expose-Headers Header", function () {
pm.response.to.have.header("Access-Control-Expose-Headers");
pm.response.to.be.header("Access-Control-Expose-Headers", headers);
});

pm.test("Date Header", function () {
pm.response.to.have.header("Date");
});

pm.test("Transfer-Encoding Header", function () {
pm.response.to.have.header("Transfer-Encoding");
pm.response.to.be.header("Transfer-Encoding", encode);
});

pm.test("X-Cache-Info Header", function () {
pm.response.to.have.header("X-Cache-Info");
pm.response.to.be.header("X-Cache-Info", info);
});

})
